import React, { useEffect } from 'react';
import './App.css';
import Papa from  'papaparse';
import UploadFileIcon from '@mui/icons-material/UploadFile';
import {useNavigate} from 'react-router-dom';

function Home({data, setData, loading, setLoading}) {
    const navigate=useNavigate();
const [values,setValues]=React.useState([]);
   const [col,setCol]=React.useState([]);

    function handleChange(event){
      alert('file uploaded!')
      Papa.parse(event.target.files[0],{
        header:true,
        skipEmptyLines:true,
        complete:function(result){
          const columnArray=[];
          const valuesArray=[];
          result.data.map((d)=>{
            columnArray.push(Object.keys(d));
            valuesArray.push(Object.values(d));
          });
          console.log(result.data)
          setData(result.data);
          setCol(columnArray[0]);
          setValues(valuesArray);
        }
      })
    }
    useEffect(()=>{
        console.log(data)
    },[data])
    function handleClick(){
        console.log(data)
        navigate('/details')
      }
  return (loading?<h2>Loading...</h2>:
   ( <div className="App">
        <div className="inputDiv">
            <label id="uploadLabel" htmlFor="fileUpload"><UploadFileIcon id="uploadLabelIcon"  />Upload CSV File</label>
            <input id="fileUpload" type="file" name="file" accept=".csv" onChange={handleChange}/>
        </div>
        <div>
            <button id="generateBtn" onClick={handleClick}>Generate PDF</button>
        </div>
    </div>)
  );
}

export default Home;
